import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable, BehaviorSubject } from 'rxjs';
import { Contact } from '../models/contact';

@Injectable({
  providedIn: 'root'
})
export class ContactService {
  private apiUrl = 'http://localhost:7115/api/contacts';
  private contacts = new BehaviorSubject<Contact[]>([]);
  public contacts$ = this.contacts.asObservable();

  constructor(private http: HttpClient) {
    this.loadContacts();
  }

  loadContacts(): void {
    this.http.get<Contact[]>(this.apiUrl).subscribe({
      next: (data) => this.contacts.next(data),
      error: (err) => console.error('Error loading contacts:', err)
    });
  }

  getContacts(): Observable<Contact[]> {
    return this.contacts$;
  }

  addContact(contact: Contact): Observable<Contact> {
    return this.http.post<Contact>(this.apiUrl, contact);
  }

  updateContact(id: number, contact: Contact): Observable<Contact> {
    return this.http.put<Contact>(`${this.apiUrl}/${id}`, contact);
  }

  deleteContact(id: number): Observable<void> {
    return this.http.delete<void>(`${this.apiUrl}/${id}`);
  }
}
