import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { ContactService } from './services/contact.service';
import { Contact } from './models/contact';

@Component({
  selector: 'app-root',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './app.component.html',
  styleUrl: './app.component.css'
})
export class AppComponent implements OnInit {
  contacts: Contact[] = [];
  selectedContact: Contact = this.createEmptyContact();
  validationErrors: { [key: string]: string } = {};
  errorMessage: string = '';
  successMessage: string = '';

  constructor(private contactService: ContactService) {}

  ngOnInit(): void {
    this.loadContacts();
  }

  createEmptyContact(): Contact {
    return {
      name: '',
      address: '',
      telephone: '',
      mobile: '',
      email: '',
      country: ''
    };
  }

  loadContacts(): void {
    this.contactService.getContacts().subscribe({
      next: (contacts) => {
        this.contacts = contacts;
        this.clearMessages();
      },
      error: (err) => {
        this.errorMessage = 'Error loading contacts: ' + (err.error?.message || err.message);
        console.error('Error loading contacts:', err);
      }
    });
  }

  newContact(): void {
    this.selectedContact = this.createEmptyContact();
    this.validationErrors = {};
    this.clearMessages();
  }

  validateFields(): boolean {
    this.validationErrors = {};

    if (!this.selectedContact.name || this.selectedContact.name.trim() === '') {
      this.validationErrors['name'] = 'Name field cannot be empty';
    }

    if (!this.selectedContact.mobile || this.selectedContact.mobile.trim() === '') {
      this.validationErrors['mobile'] = 'Mobile field cannot be empty';
    }

    if (!this.selectedContact.country || this.selectedContact.country.trim() === '') {
      this.validationErrors['country'] = 'Country field cannot be empty';
    }

    return Object.keys(this.validationErrors).length === 0;
  }

  save(): void {
    if (!this.validateFields()) {
      this.errorMessage = 'Please fill all required fields';
      return;
    }

    this.contactService.addContact(this.selectedContact).subscribe({
      next: (newContact) => {
        console.log('Contact saved:', newContact);
        this.contactService.refreshContacts();
        this.newContact();
        this.successMessage = 'Contact saved successfully';
      },
      error: (err) => {
        this.errorMessage = err.error?.message || err.message || 'Error saving contact';
        console.error('Error saving contact:', err);
      }
    });
  }

  selectContact(contact: Contact): void {
    this.selectedContact = { ...contact };
    this.validationErrors = {};
    this.clearMessages();
  }

  update(): void {
    if (!this.selectedContact.id) {
      this.errorMessage = 'No contact selected for update';
      return;
    }

    if (!this.validateFields()) {
      this.errorMessage = 'Please fill all required fields';
      return;
    }

    this.contactService.updateContact(this.selectedContact.id, this.selectedContact).subscribe({
      next: (updatedContact) => {
        const index = this.contacts.findIndex(c => c.id === updatedContact.id);
        if (index !== -1) {
          this.contacts[index] = updatedContact;
        }
        this.newContact();
        this.successMessage = 'Contact updated successfully';
      },
      error: (err) => {
        this.errorMessage = err.error?.message || 'Error updating contact';
        console.error('Error updating contact:', err);
      }
    });
  }

  delete(): void {
    if (!this.selectedContact.id) {
      this.errorMessage = 'No contact selected for deletion';
      return;
    }

    if (confirm('Are you sure you want to delete this contact?')) {
      this.contactService.deleteContact(this.selectedContact.id).subscribe({
        next: () => {
          this.contacts = this.contacts.filter(c => c.id !== this.selectedContact.id);
          this.newContact();
          this.successMessage = 'Contact deleted successfully';
        },
        error: (err) => {
          this.errorMessage = err.error?.message || 'Error deleting contact';
          console.error('Error deleting contact:', err);
        }
      });
    }
  }

  clearMessages(): void {
    setTimeout(() => {
      this.successMessage = '';
      this.errorMessage = '';
    }, 3000);
  }
}

<div class="container mt-4">
    <h3>Contact Information Management</h3>

    <!-- Alert Messages -->
    <div *ngIf="successMessage" class="alert alert-success alert-dismissible fade show" role="alert">
        {{ successMessage }}
        <button type="button" class="btn-close" (click)="successMessage = ''"></button>
    </div>
    
    <div *ngIf="errorMessage" class="alert alert-danger alert-dismissible fade show" role="alert">
        {{ errorMessage }}
        <button type="button" class="btn-close" (click)="errorMessage = ''"></button>
    </div>

    <!-- Contact Form -->
    <div class="card mb-4">
        <div class="card-header">
            <h5>Contact Form</h5>
        </div>
        <div class="card-body">
            <input type="hidden" [(ngModel)]="selectedContact.id" />

            <div class="mb-3">
                <label for="Name" class="form-label">Name <span style="color: red;">*</span></label>
                <input id="Name" 
                       class="form-control" 
                       [class.is-invalid]="validationErrors['name']"
                       placeholder="Enter full name"
                       [(ngModel)]="selectedContact.name" />
                <div *ngIf="validationErrors['name']" class="invalid-feedback d-block">
                    {{ validationErrors['name'] }}
                </div>
            </div>

            <div class="mb-3">
                <label for="Address" class="form-label">Address</label>
                <input id="Address" 
                       class="form-control" 
                       placeholder="Enter address"
                       [(ngModel)]="selectedContact.address" />
            </div>

            <div class="mb-3">
                <label for="Email" class="form-label">Email</label>
                <input id="Email" 
                       type="email"
                       class="form-control" 
                       placeholder="Enter email address"
                       [(ngModel)]="selectedContact.email" />
            </div>

            <div class="row mb-3">
                <div class="col-md-6">
                    <label for="Telephone" class="form-label">Telephone</label>
                    <input id="Telephone" 
                           class="form-control" 
                           placeholder="Enter telephone"
                           [(ngModel)]="selectedContact.telephone">
                </div>

                <div class="col-md-6">
                    <label for="Mobile" class="form-label">Mobile <span style="color: red;">*</span></label>
                    <input id="Mobile" 
                           class="form-control" 
                           [class.is-invalid]="validationErrors['mobile']"
                           placeholder="Enter mobile number"
                           [(ngModel)]="selectedContact.mobile">
                    <div *ngIf="validationErrors['mobile']" class="invalid-feedback d-block">
                        {{ validationErrors['mobile'] }}
                    </div>
                </div>
            </div>

            <div class="mb-3">
                <label for="Country" class="form-label">Country <span style="color: red;">*</span></label>
                <select id="Country" 
                        class="form-control"
                        [class.is-invalid]="validationErrors['country']"
                        [(ngModel)]="selectedContact.country">
                    <option value="">-- Select Country --</option>
                    <option value="Sri Lanka">Sri Lanka</option>
                    <option value="India">India</option>
                    <option value="United States">United States</option>
                    <option value="United Kingdom">United Kingdom</option>
                    <option value="Australia">Australia</option>
                    <option value="Canada">Canada</option>
                    <option value="New Zealand">New Zealand</option>
                </select>
                <div *ngIf="validationErrors['country']" class="invalid-feedback d-block">
                    {{ validationErrors['country'] }}
                </div>
            </div>

            <div class="button-group">
                <button class="btn btn-primary" (click)="newContact()">New</button>
                <button class="btn btn-success" (click)="save()">Save</button>
                <button class="btn btn-warning" (click)="update()" [disabled]="!selectedContact.id">Update</button>
                <button class="btn btn-danger" (click)="delete()" [disabled]="!selectedContact.id">Delete</button>
            </div>
        </div>
    </div>

    <hr />

    <!-- Contacts Table -->
    <div class="card">
        <div class="card-header">
            <h5>Contacts List</h5>
        </div>
        <div class="card-body table-responsive">
            <table class="table table-bordered table-hover" id="tbl">
                <thead class="table-dark">
                    <tr>
                        <th>Name</th>
                        <th>Address</th>
                        <th>Telephone</th>
                        <th>Mobile</th>
                        <th>Email</th>
                        <th>Country</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    <tr *ngFor="let contact of contacts">
                        <td>{{ contact.name }}</td>
                        <td>{{ contact.address }}</td>
                        <td>{{ contact.telephone }}</td>
                        <td>{{ contact.mobile }}</td>
                        <td>{{ contact.email }}</td>
                        <td>{{ contact.country }}</td>
                        <td>
                            <button class="btn btn-sm btn-primary" (click)="selectContact(contact)">Edit</button>
                        </td>
                    </tr>
                    <tr *ngIf="contacts.length === 0">
                        <td colspan="7" class="text-center text-muted">No contacts found</td>
                    </tr>
                </tbody>
            </table>
        </div>
    </div>
</div>
